import React, { Component } from "react";
import Service from "../../service/service";
import "./topRated.css";

export default class TopRated extends Component {
  service = new Service();
  state = {
    films: []
  };
  constructor() {
    super();
    this.updateFilms();
  }

  onFilmsLoaded = films => {
    this.setState({
      films
    });
  };
  updateFilms() {
    this.service.getTopRated().then(this.onFilmsLoaded);
  }
  render() {
    const { films } = this.state;
    const arr = films.map(movie => {
        console.log(movie)
      return (
        <div key={movie.id} className="mov">
          {movie.title}
          {movie.runtime}
        
        </div>
      );
    });

    return <div>{arr}</div>;
  }
}
