import React, {Component} from 'react';
import Service from '../../service/service'
import './randomFilm.css'

export default class RandomFilm extends Component{

service = new Service();

   state = {
       image: null,
       title: null,
       popularity: null,
       runtime: null,
       overview: null
 };
 
 constructor(){
     super();
     this.updateFilm();
 }

 updateFilm(){
     this.servrice
     .getPopularMovie(8)
     .then((film)=>{
         this.setState({
             image: film.poster_path,
             title: film.title,
             popularity: film.vote_average,
             runtime: film.runtime,
             overview: film.overview
         })

     })
 }

    render(){
        const{image,title,popularity,runtime,overview}=this.state
    
        return(
            <div className="random-planet jumbotron rounded">
            <img className="planet-image"
                 src="https://image.tmdb.org/t/p/w500/eQs5hh9rxrk1m4xHsIz1w11Ngqb.jpg" />
            <div>
              <h4>{title}</h4>
              <ul className="list-group list-group-flush">
                <li className="list-group-item">
                  <span className="term">Popularity:</span>
                  <span>{popularity}</span>
                </li>
                <li className="list-group-item">
                  <span className="term">Run Time:</span>
                  <span>{runtime}</span>
                </li>
                <li className="list-group-item">
                  <span className="term">Overview:</span>
                  <span>{overview}</span>
                </li>
              </ul>
            </div>
          </div>
          
        )
    }
}
