import React, { Component } from "react";
import Service from "../../service/service";
import Spinner from "../Spinner";
import "./randomFilm.css";

export default class RandomFilm extends Component {
  service = new Service();

  state = {
    film: {},
    loading: true
  };

  constructor() {
    super();
    this.updateFilm();
  }

  onFilmLoaded = film => {
    this.setState({
      film,
      loading: false
    });
  };

  onError = (err) =>{
      
  };

  updateFilm() {
    const id = Math.floor(Math.random() * 800) + 1;
    this.service
    .getPopularMovie(id)
    .then(this.onFilmLoaded)
    .catch(this.onError)
  }

  render() {
    const { film, loading } = this.state;
    const spinner = loading ? <Spinner/> : null;
    const content = !loading ? <FilmView film={film}/> : null;

    return (
      <div className="random-film jumbotron rounded">
        {spinner}
        {content}
        </div>
    );
  }
}
const FilmView = ({ film }) => {
  const { id, image, title, popularity, runtime, overview } = film;

  return (
    <React.Fragment>
      <div>
        <img
          className="film-image"
          src={`https://image.tmdb.org/t/p/w500/${image}`}
        />
      </div>
      <div>
        <h4 className="h4">{title}</h4>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">
            <span className="term">Popularity:</span>
            <span>{popularity}</span>
          </li>
          <li className="list-group-item">
            <span className="term">Run Time:</span>
            <span>{runtime}</span>
          </li>
          <li className="list-group-item">
            <span className="term">Overview:</span>
            <span>{overview}</span>
          </li>
        </ul>
      </div>
    </React.Fragment>
  );
};
