import React, { Component } from "react";
import Service from "../../service/service";
import "./topRated.css";

export default class TopRated extends Component {
  service = new Service();
  state = {
    films: []
  };
  constructor() {
    super();
    this.updateFilms();
  }

  onFilmsLoaded = films => {
    this.setState({
      films
    });
  };
  updateFilms() {
    this.service.getTopRated().then(this.onFilmsLoaded);
  }
  render() {
    const { films } = this.state;
    const arr = films.map(movie => {
        console.log(movie)
      return (
        <div key={movie.id} className="mov">
          <div>
        <img
          className="image"
          src={`https://image.tmdb.org/t/p/w500/${movie.image}`}
        />
      </div>
      <div className='container-fluid'>
        <h4 className="h4">{movie.title}</h4>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">
            <span className="term">Popularity:</span>
            <span>{movie.popularity}</span>
          </li>
          <li className="list-group-item">
            <span className="term">Run Time:</span>
            <span>{movie.runtime}</span>
          </li>
          <li className="list-group-item">
            <span className="term">Overview:</span>
            <span>{movie.overview}</span>
          </li>
        </ul>
      </div>
        
        </div>
      );
    });

    return <div>{arr}</div>;
  }
}
