import React, { Component } from "react";
import Service from "../../service/service";
import Spinner from "../Spinner"
import "./randomFilm.css";

export default class RandomFilm extends Component {
  service = new Service();

  state = {
    film: {}
  };

  constructor() {
    super();
    this.updateFilm();
  }

  onFilmLoaded=(film)=>{
      this.setState({film})


  }

  updateFilm() {
      const id = Math.floor(Math.random()*800)+1;
    this.service.getPopularMovie(id).then(this.onFilmLoaded);
  }

  render() {
    const { film: {id, image, title, popularity, runtime, overview} } = this.state;

    return (
      <div className="random-film jumbotron rounded">
      <div>
        <img
          className="film-image"
          src={`https://image.tmdb.org/t/p/w500/${image}`}
          
        />
        </div>
        <div>
          <h4 className='h4'>{title}</h4>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <span className="term">Popularity:</span>
              <span>{popularity}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Run Time:</span>
              <span>{runtime}</span>
            </li>
            <li className="list-group-item">
              <span className="term">Overview:</span>
              <span>{overview}</span>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}
